import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-employeecrud',
  templateUrl: './employeecrud.component.html',
  styleUrls: ['./employeecrud.component.scss'],
})
export class EmployeecrudComponent {
  EmployeeArray: any[] = [];
  currentEmployeeID = '';

  name: string = '';
  address: string = '';
  phone: string = '';

  constructor(private http: HttpClient) {
    this.getAllEmployee();
  }
  getAllEmployee() {
    this.http
      .get('http://localhost:8000/user/getAll')
      .subscribe((resultData: any) => {
        console.log(resultData);
        this.EmployeeArray = resultData.data;
      });
  }

  setDelete(data: any) {
    this.http
      .delete("http://localhost:8000/user/delete/" + data._id)
      .subscribe((resultData: any) => {
        console.log(resultData);
        alert('Employee deleted successfully');
        this.getAllEmployee();
      });
  }

  setUpdate(data: any) {
    this.name = data.name;
    this.address = data.address;
    this.phone = data.phone;

    this.currentEmployeeID = data._id;
  }

  UpdateRecords() {
    let bodyData = {
      name: this.name,
      address: this.address,
      phone: this.phone,
    };

    this.http
      .patch(
        'http://localhost:8000/user/update/' + this.currentEmployeeID,
        bodyData
      )
      .subscribe((resultData: any) => {
        console.log(resultData);
        alert('Employee updated successfully');
        this.getAllEmployee();
        this.name = '';
        this.address = '';
        this.phone = '';
      });
  }

  save() {
    if (this.currentEmployeeID == '') {
      this.register();
    } else {
      this.UpdateRecords();
    }
  }

  register() {
    let bodyData = {
      name: this.name,
      address: this.address,
      phone: this.phone,
    };

    this.http
      .post('http://localhost:8000/user/create', bodyData)
      .subscribe((resultData: any) => {
        console.log(resultData);
        alert('Student registered successfully');
        this.getAllEmployee();
        this.name = '';
        this.address = '';
        this.phone = '';
      });
  }
}
<style>
    table,th,td {

        border: 2px solid black;
    }
</style>

<div style="border: 3px solid black" class="container mt-4">
  <div style="border: none; padding: 15px" class="card">
    <h1 style="margin: auto">Employee Registration</h1>

    <form style="">
      <div class="mb-3">
        <label style="font-weight: bold" class="form-label">Name </label>
        <input
          type="text"
          [(ngModel)]="name"
          [ngModelOptions]="{ standalone: true }"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your Name"
        />
      </div>
      <div class="mb-3">
        <label style="font-weight: bold" class="form-label">Address </label>
        <input
          type="text"
          [(ngModel)]="address"
          [ngModelOptions]="{ standalone: true }"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your address"
        />
      </div>
      <div class="mb-3">
        <label style="font-weight: bold" class="form-label">Phone </label>
        <input
          type="text"
          [(ngModel)]="phone"
          [ngModelOptions]="{ standalone: true }"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your Phone number"
        />
      </div>

      <button style="margin-left: 47%;" type="submit" (click)="save()" class="btn btn-primary">
        Submit
      </button>
    </form>
  </div>
</div>

<div style="border: 3px solid black;" class="container mt-4">
  <div style="padding: 15px;border: none;" class="card">
    <h1 style="margin: auto">Employee Table</h1>
    <table style="text-align: center; padding: 15px; margin-top: 30px;" class="table">
      <thead>
        <tr>
          <th scope="col">Name</th>
          <th scope="col">Address</th>
          <th scope="col">Phone</th>
          <th scope="col">Option</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let Employee of EmployeeArray">
          <td>{{ Employee.name | uppercase }}</td>
          <td>{{ Employee.address }}</td>
          <td>{{ Employee.phone }}</td>
          <td style="">
            <button
              style="margin-right: 10px; border-radius: ;"
              type="button"
              class="btn btn-success"
              (click)="setUpdate(Employee)"
            >
              Edit
            </button>
            <button
              type="button"
              class="btn btn-danger"
              (click)="setDelete(Employee)"
            >
              Delete
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
