import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-employeecrud',
  templateUrl: './employeecrud.component.html',
  styleUrls: ['./employeecrud.component.scss']
})
export class EmployeecrudComponent {

  name : string =""
  address : string =""
  phone: string = ""

  constructor(private http: HttpClient) {

  }

  register() {

    let bodyData = {

      "name": this.name,
      "address": this.address,
      "phone": this.phone
    };

    this.http.post("http://localhost:8000/user/create", bodyData).subscribe((resultData: any) => {

      console.log(resultData);
      alert("Student registered successfully");

      this.name = '';
      this.address = '';
      this.phone = '';
    });
  }
}
<div class="container mt-4">
  <div class="card">
    <h1>Employee Registration</h1>

    <form>
      <div class="mb-3">
        <label class="form-label">Name </label>
        <input
          type="text" [(ngModel)]="name" [ngModelOptions]="{standalone: true}"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your Name"
        />
      </div>
      <div class="mb-3">
        <label class="form-label">Address </label>
        <input
          type="text" [(ngModel)]="address" [ngModelOptions]="{standalone: true}"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your address"
        />
      </div>
      <div class="mb-3">
        <label class="form-label">Phone </label>
        <input
          type="text" [(ngModel)]="phone" [ngModelOptions]="{standalone: true}"
          class="form-control"
          id="exampleInputEmail1"
          aria-describedby="emailHelp"
          placeholder="Enter your Phone number"
        />
      </div>

      <button type="submit" (click)="register()" class="btn btn-primary">Submit</button>
    </form>
  </div>
</div>
