import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-employeecrud',
  templateUrl: './employeecrud.component.html',
  styleUrls: ['./employeecrud.component.scss']
})
export class EmployeecrudComponent {

  EmployeeArray : any[] = [];
  name : string =""
  address : string =""
  phone: string = ""

  constructor(private http: HttpClient) {

    this.getAllEmployee();
  }
  getAllEmployee() {

    this.http.get("http://localhost:8000/user/getAll").subscribe((resultData: any) => {

      console.log(resultData);
      this.EmployeeArray = resultData.data;
    })
  }

  register() {

    let bodyData = {

      "name": this.name,
      "address": this.address,
      "phone": this.phone
    };

    this.http.post("http://localhost:8000/user/create", bodyData).subscribe((resultData: any) => {

      console.log(resultData);
      alert("Student registered successfully");

      this.name = '';
      this.address = '';
      this.phone = '';
    });
  }
}
